import {useParams} from "react-router-dom";

//need to include an if statement that checks the value of the URL ID and then

//renders the logo styling conditionally as hover or not hovered.
//This is to make sure that when the case study page load the chosen case study
//page has it's logo in colour in the projects navigation.

function LinkStyle() {
  let {project} = useParams();
  if(project === "JasonClerkID"){
    return "jclogo:hover";
  }
  else return "jclogo";
}
export default LinkStyle;
