import React from "react";
import {Link} from 'react-router-dom';
import {useParams} from "react-router-dom";
import CaseThumbnails from "./Components/CaseThumbnails";

var CaseData = require('./Components/Data Modules/CaseData'); //data set
// CaseData[i].URLquery
//need to write a function that will search through the data to find the object with the corresponding data
//find the correct project object and then use JSX to display the content
//on the page



function CaseStudy() {
  return (
    <div className="caseStudy">
      <Link to="/">Return Home</Link>
      <div className="caseStudies">
            <div id="jclogo" className="card" onClick={()=>{navigate("/work/JasonClerkID")}}></div>
            <div id="sflogo" className="card" onClick={()=>{navigate("/work/SugarFlower")}}></div>
            <div id="canada150logos" className="card" onClick={()=>{navigate("/work/Canada150")}}></div>
            <div id="McDslogo" className="card" onClick={()=>{navigate("/work/McDonaldsGCTA")}}></div>
        </div>
      <CaseThumbnails />
      <DisplayCaseStudy />
    </div>
  );
}

function DisplayCaseStudy (){
  let {project} = useParams();
  const found = CaseData.find(element => element.URLquery === project);
  return <div className="caseStudy__detail">
  <div className="caseStudy__detail-name">{found.projName}</div>
  <h4>{found.bodyCopy}</h4>
  </div>
}

export default CaseStudy;
