import React from "react";
import {Link} from 'react-router-dom';
import {useParams} from "react-router-dom";
// import CaseName from "./Components/CaseName";
// import CaseSplash from "./Components/CaseSplash";
// import CaseHeading from "./Components/CaseHeading";
// import CaseBody from "./Components/CaseBody";
import CaseJC from "./Components/CaseJC";
import CaseSF from "./Components/CaseSF";
import CaseCanada150 from "./Components/CaseCanada150";
// import CaseThumbnails from "./Components/CaseThumbnails";
import CaseMcDs from "./Components/CaseMcDs";
import Back from "./Components/Back"
import Next from "./Components/Next";

var CaseData = require('./Components/Data Modules/CaseData'); //data set
// CaseData[i].URLquery
//need to write a function that will search through the data to find the object with the corresponding data
//find the correct project object and then use JSX to display the content
//on the page



function CaseStudy() {
  let {project} = useParams();
  const found = CaseData.find(element => element.URLquery === project);
  //here from props I know what project I am currently on, it is "found"
  //now i need to search through the array to locate the next object in the array after the current one
  //i could use the indexOf found and add 1


  console.log(indexCurrentProj);

  nextProject = () =>{
  let {project} = useParams();
  const currentProj = CaseData.find(element => element.URLquery === project);
  const indexCurrentProj = CaseData.indexOf(currentProj);
    if(indexCurrentProj === 0){
      return "/work/SugarFlower";
    }
    else if(indexCurrentProj === 1){
      return "/work/Canada150";
    }
    else if(indexCurrentProj === 2){
      return "/work/McDonaldsGCTA";
    }
    else if(indexCurrentProj === 3){
      return "/work/JasonClerkID";
    } 
  }


  return (
    <div className="caseStudy">
      <div className="navLinks">
        <Link to="/"><Back /></Link>
        <Link to={this.nextProject()}><Next /></Link>
      </div>
      <DisplayCaseStudy />
    </div>
  );
}
function DisplayCaseStudy (){
  let {project} = useParams();
  if(project === "JasonClerkID"){
    return (
      <CaseJC />
    )
  }
  else if(project === "SugarFlower"){
    return (
      <CaseSF />
    )
  }
  else if(project === "Canada150"){
    return (
      <CaseCanada150 />
    )
  }
  else if(project === "McDonaldsGCTA"){
    return (
      <CaseMcDs />
    )
  }
  else return console.log("error")
}

export default CaseStudy;
