import React from "react";
import Burger from "./Burger";
import Close from "./Close";


function Navigation() {

    function DisplayMenuIcon (){
        const [mobileMenu, setViewStatus] = useState(false);
        if (mobileMenu === false){
            return<div className="navigation__burger" onClick={() => setViewStatus(true)}><Burger className="navigation__burger-icon"alt="burger menu icon" /></div>
        }
        else {
            return<div className="navigation__close" onClick={() => setViewStatus(false)}><Close className="navigation__close-icon"alt="burger menu icon" /></div>
        }
    }

    function DisplayMobileMenuOpen (){
        const [mobileMenu, setViewStatus] = useState(false);

        if (mobileMenu === true){
            return<div className="navigation__menu-mobileOpen">
                <div className="navigation__menu-item"><a href="#about-me" onClick={() => setViewStatus(false)}>About Me</a></div>
                <div className="navigation__menu-item"><a href="#work" onClick={() => setViewStatus(false)}>Work</a></div>
                <div className="navigation__menu-item"><a href="#testimonials" onClick={() => setViewStatus(false)}>Testimonials</a></div>
                <div className="navigation__menu-item"><a href="#resume" onClick={() => setViewStatus(false)}>Resume</a></div>
            </div>
        }
        else {
            return <div className="navigation__menu-mobileClosed">
            <div className="navigation__menu-item"><a href="#about-me">About Me</a></div>
            <div className="navigation__menu-item"><a href="#work">Work</a></div>
            <div className="navigation__menu-item"><a href="#testimonials">Testimonials</a></div>
            <div className="navigation__menu-item"><a href="#resume">Resume</a></div>
        </div>
        }
    }
    return (
        <div className="navigation">
        {DisplayMenuIcon()}
        {DisplayMobileMenuOpen()}
    {/* <div className="navigation__menu">
        <div className="navigation__menu-item"><a href="#about-me">About Me</a></div>
        <div className="navigation__menu-item"><a href="#work">Work</a></div>
        <div className="navigation__menu-item"><a href="#testimonials">Testimonials</a></div>
        <div className="navigation__menu-item"><a href="#resume">Resume</a></div>
    </div> */}

    </div>
    );
}

export default Navigation;
